import React from 'react';
import { useRouter } from 'next/router';

const imageGallery = [
  { id: 1, title: 'Event 1', imageUrl: 'https://ibb.co/2vrBpCn' },
  { id: 2, title: 'Event 2', imageUrl: 'https://ibb.co/LPswc47' },
  { id: 3, title: 'Event 3', imageUrl: 'https://ibb.co/WKPHcgX' },
];

const Events = () => {
  const router = useRouter();

  const handleImageClick = (id) => {
    router.push(`/events/${id}`);
  };

  return (
    <div className='events grid grid-cols-1 md:grid-cols-3 gap-6 p-6'>
      {imageGallery.map((item) => (
        <div
          key={item.id}
          className='group cursor-pointer'
          onClick={() => handleImageClick(item.id)}
        >
          <img
            src={item.imageUrl}
            alt={item.title}
            className='w-full h-64 object-cover rounded-md shadow-md group-hover:opacity-90'
          />
          <h4 className='text-center mt-2 text-lg font-semibold'>{item.title}</h4>
        </div>
      ))}
    </div>
  );
};

export default Events;
