import React from 'react';
import { useParams } from 'react-router-dom';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';

const imageGallery = [
  { id: 1, title: 'Event 1', imageUrl: 'https://i.ibb.co/ZBrCbFL/20241130-204423.jpg' },
  { id: 2, title: 'Event 2', imageUrl: '/images/event2.jpg' },
  { id: 3, title: 'Event 3', imageUrl: '/images/event3.jpg' },
];

const EventDetails = () => {
  const { id } = useParams();
  const currentEvent = imageGallery.find((event) => event.id === parseInt(id));

  if (!currentEvent) return <p>Event not found.</p>;

  return (
    <div className='p-6'>
      <h1 className='text-2xl font-bold text-center mb-6'>{currentEvent.title}</h1>
      <Swiper spaceBetween={10} slidesPerView={1} loop>
        {imageGallery.map((item) => (
          <SwiperSlide key={item.id}>
            <img
              src={item.imageUrl}
              alt={item.title}
              className='w-full object-contain rounded-md'
            />
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default EventDetails;
