import React from 'react';
import { Link } from 'react-router-dom';

const Cards = () => {
  const cardsData = [
    { id: 1, frontContent: `L.I.F.E.'s Alternative: L.E.A.D. (Let Everyone Advance with Dignity) Diversion Program`, link: 'program3' },
    { id: 2, frontContent: 'Back2LIFE Reintegration & Transition Support Program', link: 'program1' },
    { id: 3, frontContent: '1Life2Live Gun & Gang Violence Prevention & Intervention Program', link: 'program2' }
  ];

  const handleCardClick = (programId) => {
    console.log('Clicked on card with programId:', programId);
  };

  return (
    <div className="c-section">
      <h2 className="c-section-h">Our Programs</h2>
      <div className="card-container">
        {cardsData.map(card => (
          <Link
            to={`/programs#${card.link}`}
            key={card.id}
            onClick={() => handleCardClick(card.link)}
          >
            <div className="card">
              <i className="fa-regular fa-flag"></i>
              <h2>{card.frontContent}</h2>
              <p>Read More</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Cards;
