import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Cards = () => {
  const [isFlipped, setIsFlipped] = useState(false);

  const cardsData = [
    { id: 1, frontContent: `L.I.F.E.'s Alternative: L.E.A.D. (Let Everyone Advance with Dignity) Diversion Program`, backContent: 'Read More', sectionId:'program-3'},
    { id: 2, frontContent: 'Back2LIFE Reintegration & Transition Support Program', backContent: 'Read More', sectionId: 'program-1' },
    { id: 3, frontContent: '1Life2Live Gun & Gang Violence Prevention & Intervention Program', backContent: 'Read More', sectionId: 'program-2' }
  ];

  return (
    <div className='c-section'>
      <h2 className='c-section-h'>Our Programs</h2>
      <div className='card-container'>
        {cardsData.map(card => (
          <Link to={`/programs#${card.sectionId}`} key={card.id}>
            <div
              className={`card ${isFlipped ? 'is-flipped' : ''}`}
              onMouseEnter={() => setIsFlipped(true)}
              onMouseLeave={() => setIsFlipped(false)}
            >
              <div className="card-face front-face">
                <i className="fa-regular fa-flag"></i>
                <h2>{card.frontContent}</h2>
              </div>
              <div className="card-face back-face">
                <h2>{card.backContent}</h2>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Cards;
